document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("burger"),t=document.getElementById("menu"),n=document.querySelectorAll(".menu__list-item"),o=document.querySelector("body");e.addEventListener("click",(function(){e.classList.toggle("active"),t.classList.toggle("open"),o.classList.toggle("lock"),n.forEach(e=>e.classList.toggle("open"))})),document.querySelectorAll(".menu li a").forEach(e=>{window.location.pathname.indexOf(e.getAttribute("href"))>-1&&e.classList.add("active")}),window.addEventListener("scroll",()=>{document.getElementById("header").classList.toggle("fixed",window.scrollY>30)})});